mapper.select:
select *
from university
where is_deleted=0

    and university_name like concat('%',#{universityName},'%')


    and university_code = #{universityCode}


    and university_desc like concat('%',#{universityDesc},'%')


    and is_211= #{is211}


    and is_985= #{is985}


    and is_double_class= #{isDoubleClass}


    and is_public= #{isPublic}


    and is_undergraduate= #{isUndergraduate}


    and province like concat(#{province},'%')


    and city like concat(#{city},'%')
